package lab_exercise;
public class string_5fun {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String b="Khaleel";
		String a="Mohammed "; String n="CHARACTER";
		System.out.println("Character at 4th pos: "+b.charAt(1));
		System.out.println("Concatination of 2 strings are: "+a.concat(b));
		System.out.println("if both are equal: "+a.equals(b));
		System.out.println("Length of string 1: "+a.length());
		System.out.println("Length of string 2: "+b.length());
		System.out.println("1st string in upper case: "+a.toUpperCase());
		System.out.println("new string to lower case: "+n.toLowerCase());
	
	}
}